# Import required moduleimport numpy as npfrom sklearn.neighbors import NearestNeighborsfrom sklearn.cluster import DBSCANimport matplotlib.pyplot as pltfrom sklearn import metricsdef optimal_eps_DBSCAN(X, K):    """        Parameters    ----------    X : [numpy array]    Returns    -------    Visualization to determine the eps    """    neigh = NearestNeighbors(n_neighbors = K)    nbrs = neigh.fit(X)    distances, indices = nbrs.kneighbors(X)    distances = np.sort(distances, axis=0)    distances = distances.sum(axis = 1)    plt.plot(distances)